function App() {
  const { token, setToken } = useToken();



  if(!token){
    return <Login setToken={setToken}  />
  }

  return (
    <Router>
      <div className="App">
        <h1 className="h1">Welcome artist</h1>
        <Switch>
          <Route path="/signup" component={Signup} />
          <Route path="/canvas" component={Canvas} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
