function App() {
  const [ isLoggedIn, setLoggedIn] = useState(false)
  const checkToken = () => {
    const userToken = localStorage.getItem('token')
    if(!userToken || userToken === undefined){
      setLoggedIn(false)
    }
  }
  useEffect(() => {
    checkToken()
  }, [isLoggedIn])




  return (
    <Router>
      <div className="App">
        <h1 className="h1">Welcome artist</h1>
        <Switch>
          <Route path="/signup" component={Signup} />
          <Route
            path="/canvas"
            render={() => (isLoggedIn ? <Canvas /> : <Redirect to="/signup" />)}
          />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
