import React, { Component } from 'react';
import CanvasDraw from 'react-canvas-draw';

class Canvas extends Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
  }

  handleCreateNewCanvas = () => {
    const canvasData = this.canvasRef.current.getSaveData();
    // Call the function in the parent component to pass the canvas drawing
    this.props.onNewCanvas(canvasData);
    // Clear the canvas drawing
    this.canvasRef.current.clear();
  }



  render() {
    return (
      <div>
        <h2>Canvas Component</h2>
        <CanvasDraw
          ref={this.canvasRef}
          // Other props for configuring the canvas
        />
        <button onClick={this.handleCreateNewCanvas}>Create New Canvas</button>
      </div>
    );
  }
}

export default Canvas;
