import React, { Component } from 'react';
import Canvas from './Canvas';

class ParentComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      canvasData: null
    };
  }

  updateCanvasData = (newData) => {
    this.setState({ canvasData: newData });
  }

  handleNewCanvas = (canvasData) => {
    // Handle the creation of a new canvas and use the canvas data passed from the previous canvas
    console.log('New Canvas Data:', canvasData);
    // Perform any necessary operations with the canvas data

    // Optional: Clear the previous canvas data
    this.setState({ canvasData: null });
  }

  render() {
    const { canvasData } = this.state;

    return (
      <div>
        <h1>Parent Component</h1>
        {canvasData ? (
          <div>
            <h2>Previous Canvas Drawing:</h2>
            <img src={canvasData} alt="Previous Canvas" />
          </div>
        ) : null}
        <Canvas onNewCanvas={this.handleNewCanvas} />
      </div>
    );
  }
}

export default ParentComponent;
