import React, { Component } from 'react';
import CanvasDraw from 'react-canvas-draw';

class Canvas extends Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
  }

  handleNewCanvas = (canvasData) => {
    // Handle the creation of a new canvas and use the canvas data passed from the previous canvas
    console.log('New Canvas Data:', canvasData);
    // Perform any necessary operations with the canvas data

    // Optional: Clear the previous canvas data
    this.setState({ canvasData: null });
  }


  render() {
    return (
      <div>
        <h2>Canvas Component</h2>
        <CanvasDraw
          ref={this.canvasRef}
          // Other props for configuring the canvas
        />
        <button onClick={this.handleCreateNewCanvas}>Create New Canvas</button>
      </div>
    );
  }
}

export default Canvas;
