import React, { useState, useRef } from 'react';

function Canvas() {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [canvasData, setCanvasData] = useState(null);

  const startDrawing = (e) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { offsetX, offsetY } = e.nativeEvent;

    ctx.beginPath();
    ctx.moveTo(offsetX, offsetY);
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { offsetX, offsetY } = e.nativeEvent;

    ctx.lineTo(offsetX, offsetY);
    ctx.stroke();
  };

  const stopDrawing = () => {
    setIsDrawing(false);
  };

  const handleCreateNewCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setCanvasData(null);
  };

  const handleGetProperties = () => {
    if (canvasData) {
      console.log('Canvas Data:', canvasData);
      // You can pass the canvasData to another function or perform any other operations with it
    } else {
      console.log('Canvas data is not available.');
    }
  };

  const handleSaveCanvasData = () => {
    const canvas = canvasRef.current;
    const dataURL = canvas.toDataURL();
    setCanvasData(dataURL);
    console.log('Canvas data saved:', dataURL);
  };

  return (
    <div>
      <h2>Canvas Component</h2>
      <canvas
        ref={canvasRef}
        width={400}
        height={400}
        style={{ backgroundColor: 'lightgray' }}
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseOut={stopDrawing}
      />
      <button onClick={handleCreateNewCanvas}>Create New Canvas</button>
      <button onClick={handleGetProperties}>Get Properties</button>
      <button onClick={handleSaveCanvasData}>Save Canvas Data</button>
    </div>
  );
}

export default Canvas;
