import React, { useRef, useState } from 'react';
import CanvasDraw from 'react-canvas-draw';
import clear from ''

const Canvas = () => {
  const canvasRef = useRef(null);
  const [canvasKey, setCanvasKey] = useState(0);

  const handleCreateNewCanvas = () => {
    setCanvasKey(prevKey => prevKey + 1);
  };

  const clearCanvas = () => {
   if(canvasRef){
    clear()
   }
  };

  const nextCAnvas = () => {

    if(canvasRef.current.props.brushColor ==='#444'){

        canvasRef.current.props = {brushColor: "#ff0000" } // Update to the new brush color
    }else {
        canvasRef.current.props = {brushColor: "#444" }
    }

  }

  const saveCanvas = () => {
    if (canvasRef) {
      const canvasProps = Object.assign({}, canvasRef.current.props);
      localStorage.setItem("canvasProps", JSON.stringify(canvasProps));
    }
  }

  const isEmptyCanvas = (canvas) => {
    const context = canvas.getContext('2d');
    const canvasData = context.getImageData(0, 0, canvas.width, canvas.height).data;

    // Check if any pixel in the canvas data has a non-zero alpha value
    for (let i = 3; i < canvasData.length; i += 4) {
      if (canvasData[i] !== 0) {
        return false;
      }
    }

    return true;
  };

  const get = () => {
    const canvasProps = localStorage.getItem('canvasProps');
    if (canvasProps) {
      const parsedCanvasProps = JSON.parse(canvasProps);
      console.log(parsedCanvasProps);
    }
  }

  const print  = () => {
    if(isEmptyCanvas(canvasRef.current)){
        console.log('hey ya')

    }

  }





  return (
    <div>
      <h1>Canvas Component</h1>
      <button onClick={handleCreateNewCanvas}>Create New Canvas</button>
      <button onClick={clearCanvas}>Clear Canvas</button>
      <button onClick={nextCAnvas}>Change Red/Blacn</button>
      <button onClick={saveCanvas}>Save</button>
      <button onClick={get}>get</button>
      <button onClick={print}>print</button>
      <CanvasDraw ref={canvasRef} key={canvasKey} />
    </div>
  );
};

export default Canvas;
