import React, { useRef, useState } from 'react';
import CanvasDraw from "react-canvas-draw";

const Canvas = () => {
  const canvasRef = useRef(null);
  const [key, setKey] = useState(0);

  const handleCreateNewCanvas = () => {
    setKey(prevKey => prevKey + 1);
    clearCanvas();
  };

  const clearCanvas = () => {
    canvasRef.current.clear();
  };

  const undo = () => {
    if(canvasRef)
    canvasRef.current.undo();
  };

  const getSaveData = () => {
    return canvasRef.current.getSaveData();
  };

  const loadSaveData = (saveData, immediate = false) => {
    canvasRef.current.loadSaveData(saveData, immediate);
  };

  const getDataURL = (fileType, useBgImage, backgroundColour) => {
    return canvasRef.current.toDataURL(fileType, useBgImage, backgroundColour);
  };

  return (
    <div>
      <h1>Canvas Component</h1>
      <button onClick={handleCreateNewCanvas}>Create New Canvas</button>
      <button onClick={clearCanvas}>Clear Canvas</button>
      <button onClick={undo}>Undo</button>
      <button onClick={() => console.log(getSaveData())}>Get Save Data</button>
      <button onClick={() => loadSaveData('{"lines":[],"width":400,"height":400}', true)}>Load Save Data</button>
      <button onClick={() => console.log(getDataURL())}>Get Data URL</button>
      <CanvasDraw key={key} ref={canvasRef} />
    </div>
  );
};

export default Canvas;
