import React, { useRef, useState } from 'react';
import CanvasDraw from 'react-canvas-draw';

const Canvas = () => {
  const [saveData, setSaveData] = useState(null);
  const canvasRef = useRef(null);

  const handleGetSaveData = () => {
    const data = canvasRef.current.getSaveData();
    setSaveData(data);
  };

  const handleLoadSaveData = () => {
    if (saveData) {
      const parsedSaveData = JSON.parse(saveData); // Ensure saveData is a valid JSON string
      canvasRef.current.loadSaveData(parsedSaveData, true);
    }
  };

  const handleCreateNewCanvas = () => {
    setSaveData(null);
    canvasRef.current.clear();
  };

  return (
    <div>
      <h1>Canvas Component</h1>
      {saveData && (
        <button onClick={handleLoadSaveData}>Load Previous Drawing</button>
      )}
      <button onClick={handleGetSaveData}>Get Save Data</button>
      <button onClick={handleCreateNewCanvas}>Create New Canvas</button>
      <CanvasDraw ref={canvasRef} saveData={saveData} />
    </div>
  );
};

export default Canvas;
