import React, { useState, useEffect, useRef, useContext  } from 'react';
import '../styles/canvas.css';
import { UserContext } from '../habdler/userContext';

function Canvas() {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [width, setWidth] = useState(400);
  const [savedImageData, setSavedImageData] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [store, setStore] = useState([])
  const [index,  setIndex] = useState(0)
  const { user ,setUser } = useContext(UserContext);
  const [isRetrieveData, setIsRetrieveData] = useState(false);


  useEffect(() => {
    if (savedImageData) {
      handleLoadData();
    }
  }, [savedImageData]);

  const startDrawing = (e) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { offsetX, offsetY } = e.nativeEvent;

    ctx.beginPath();
    ctx.moveTo(offsetX, offsetY);
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { offsetX, offsetY } = e.nativeEvent;

    ctx.lineTo(offsetX, offsetY);
    ctx.stroke();
  };

  const stopDrawing = () => {
    setIsDrawing(false);
  };

  const saveD = async () => {
    if (canvasRef.current) {
      const canvas = canvasRef.current;
      const dataURL = canvas.toDataURL();

      setSavedImageData(dataURL);
      setStore(prevStore => [...prevStore, dataURL]);
      console.log(dataURL);
      console.log('data saved');
    }
  };


  const clearData = () => {
    const canvas = canvasRef.current
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
    console.log('data cleared ')
    return true 
  };

  const handleLoadData = () => {
    if (savedImageData) {
      setImageUrl(savedImageData);
      console.log(savedImageData)
      setIsRetrieveData(true);
      setIsDrawing(false); // Reset the drawing state
      console.log('data loaded ')

    }
  };

  const handleLoadData2 = () => {
    return new Promise((resolve) => {
      if (savedImageData) {
        setImageUrl(savedImageData);
        setIsRetrieveData(true);
        setIsDrawing(false);
        console.log('data loaded');
        resolve(); // Resolve the promise when image URL is set
      }
    });
  };




  const retrieveData = () => {
    let index = 0
    if(store.length > 0){
      setIsRetrieveData(false);



      const interval = setInterval(() => { 
        console.log(imageUrl.opacity)
        setImageUrl(store[index])
        index++
        if (index === store.length) {
          clearInterval(interval);

          // Clear the interval when reaching the end of the array
        }
      }, 1000);

      return () => {
        clearInterval(interval)
      }
    }
  }

  const multiFunc = () => {
    const DOUBLE_CLICK_DELAY = 300; // Adjust the delay as needed

    let clickTimer = null;

    if (clickTimer) {
      clearTimeout(clickTimer); // Cancel the previous timer
      clickTimer = null;

      // Double-click behavior
      saveD();
      clearData();
      handleLoadData();
    } else {
      // Start the timer to wait for the second click
      clickTimer = setTimeout(() => {
        clickTimer = null;

        // Single-click behavior
        // Add your single-click logic here
        console.log('Single click');
      }, DOUBLE_CLICK_DELAY);
    }
  };











  return (
    <div>
      {user ?  <h2> Draw {user.name}</h2> : <h2> Draw bb</h2>}
      <div style={{ position: 'relative', width: '400px', height: '400px' }}>
        <canvas
          ref={canvasRef}
          width={width}
          height={400}
          style={{

            position: 'absolute',
            top: 0,
            left: 0,
            backgroundColor: 'lightgray',
          }}
          onMouseDown={startDrawing}
          onMouseMove={draw}
          onMouseUp={stopDrawing}
          onMouseOut={stopDrawing}

        />
          {imageUrl && (
<img
  src={imageUrl}
  alt='baby'
  style={{
    position: 'absolute',
    top: 0,
    left: 0,
    opacity: isRetrieveData ? 0.1 : 1,
  }}
  draggable="false" 
  onMouseDown={startDrawing}
  onMouseMove={draw}
  onMouseUp={stopDrawing}
  onMouseOut={stopDrawing}
/>

  )}

      </div>
      <button onClick={saveD}>Save Drawing</button>
      <button onClick={retrieveData}>Animate</button>
      <button onClick={clearData}>Clear</button>
      <button onClick={handleLoadData}>Show Saved Image</button>
      <button onClick={multiFunc}>next</button>
    </div>
  );
}

export default Canvas;
