import React, { useRef } from 'react';
import CanvasDraw from 'react-canvas-draw';

const Canvas = () => {
  const canvasRef = useRef(null);

  const handleGetSaveData = () => {
    const saveData = canvasRef.current.getSaveData();
    console.log('Save Data:', saveData);
  };

  const handleLoadSaveData = () => {
    const saveData = '{"lines":[{"color":"#000000","opacity":0.5,"points":[{"x":15,"y":57,"time":1659799},{"x":...' // Provide the save data string here
    const immediate = true; // Set to true to load the save data immediately
    canvasRef.current.loadSaveData(saveData, immediate);
  };

  const handleGetDataURL = () => {
    const fileType = 'png'; // Specify the file type, e.g., 'png', 'jpeg'
    const useBgImage = false; // Set to true to include the background image in the exported image
    const backgroundColour = '#ffffff'; // Specify the background color of the exported image
    const dataURL = canvasRef.current.getDataURL(fileType, useBgImage, backgroundColour);
    console.log('Data URL:', dataURL);
  };

  const handleClear = () => {
    canvasRef.current.clear();
  };

  const handleEraseAll = () => {
    canvasRef.current.eraseAll();
  };

  const handleResetView = () => {
    canvasRef.current.resetView();
  };

  const handleUndo = () => {
    canvasRef.current.undo();
  };

  return (
    <div>
      <h1>Canvas Component</h1>
      <CanvasDraw ref={canvasRef} />
      <button onClick={handleGetSaveData}>Get Save Data</button>
      <button onClick={handleLoadSaveData}>Load Save Data</button>
      <button onClick={handleGetDataURL}>Get Data URL</button>
      <button onClick={handleClear}>Clear</button>
      <button onClick={handleEraseAll}>Erase All</button>
      <button onClick={handleResetView}>Reset View</button>
      <button onClick={handleUndo}>Undo</button>
    </div>
  );
};

export default Canvas;
