import React, { useRef, useState } from 'react';
import CanvasDraw from 'react-canvas-draw';

const Canvas = () => {
  const canvasRef = useRef(null);
  const [canvasKey, setCanvasKey] = useState(0);

  const handleCreateNewCanvas = () => {
    setCanvasKey(prevKey => prevKey + 1);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current.canvas.drawing;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
    canvasRef.current.clear();
  };

  const nextCAnvas = () => {

    if(canvasRef.current.props.brushColor ==='#444'){

        canvasRef.current.props = {brushColor: "#ff0000" } // Update to the new brush color
    }else {
        canvasRef.current.props = {brushColor: "#444" }
    }

  }
  const saveCanvas = () => {
    if (canvasRef.current) {
      const canvasState = {
        brushColor: canvasRef.current.props.brushColor,
        // Include any other properties or data from the canvasRef object that you want to save
      };
      localStorage.setItem("canvasState", JSON.stringify(canvasState));
    }
  }


  const get = () => {
    const canvasState = localStorage.getItem('canvasState');
    if (canvasState) {
      const savedState = JSON.parse(canvasState);
      if (canvasRef.current) {
        canvasRef.current.props.brushColor = savedState.brushColor;
        // Restore any other properties or data from the savedState object to the canvasRef object
      }
      console.log(canvasRef.current.props.brushColor);
    }
  }






  return (
    <div>
      <h1>Canvas Component</h1>
      <button onClick={handleCreateNewCanvas}>Create New Canvas</button>
      <button onClick={clearCanvas}>Clear Canvas</button>
      <button onClick={nextCAnvas}>Change Red/Blacn</button>
      <button onClick={saveCanvas}>Save</button>
      <button onClick={get}>get</button>
      <CanvasDraw ref={canvasRef} key={canvasKey} />
    </div>
  );
};

export default Canvas;
